mapper.sql:
select a.`id`, a.`customer_name` customerName, a.`customer_tel` customerTel, a.`customer_level` customerLevel, case when a.`customer_level`=1 then '省级代理' when a.`customer_level`=2 then '市级代理' when a.`customer_level`=3 then '特约代理' when a.`customer_level`=4 then '终端代理' when a.`customer_level`=5 then '普通客户' else '其他' end customerLevelName, a.`address`, a.`modify_time` modifyTime, a.`create_time` createTime, ifnull(b.cnt,0) totalOrder, ifnull(b.really_pay,0) totalCost from t_snack_customer_info a left join( select customer_id, count(1) cnt, sum(really_pay) really_pay from t_snack_sale_info group by customer_id )b on a.id=b.customer_id where 1=1
and a.`id` = #{id,jdbcType=DECIMAL}
and a.`customer_name` like concat('%',#{customerName,jdbcType=VARCHAR},'%')

mapper.select:
   
	   select a.*
		          from (
	 
			         
    
		   ) a
		   order by totalOrder desc
		   limit #{lbound},#{pageSize}
